import axios from "axios";
import React from "react";
const Matches = ({ username, matches = [] }) => {
    const handleShortlist = async (matchName) => {
    try {
      await axios.patch(`http://localhost:3001/users/${username}/shortlist`, {
        matchName,
      });
      alert(`${matchName} shortlisted!`);
    } catch (err) {
      console.error(err);
      alert("Failed to shortlist user.");
    }
  };

  return (
    <div>
      <h2>Matches for {username}</h2>
      {matches.length === 0 ? (
        <p>No matches found with common interests.</p>
      ) : (
        matches.map((match) => (
          <div key={match.name}>
            <strong>{match.name}</strong>: {match.interests.join(", ")} {" "}
            <button onClick={() => handleShortlist(match.name)}>Shortlist</button>
          </div>
        ))
      )}
    </div>
  );
};

export default Matches;
